CREATE VIEW `team_rescue_assist` AS select `p`.`player_name` AS `player_name`,`p`.`common_role1` AS `common_role1`,`p`.`common_role2` AS `common_role2`,`p`.`team_name` AS `team_name` from ((`player` `p` join `role` `r1` on((`p`.`common_role1` = `r1`.`role_name`))) join `role` `r2` on((`p`.`common_role2` = `r2`.`role_name`))) where (((`r1`.`role_definition1` = '辅助') and (`r2`.`role_definition1` = '对抗路')) or ((`r1`.`role_definition2` = '辅助') and (`r2`.`role_definition1` = '对抗路')) or ((`r1`.`role_definition1` = '辅助') and (`r2`.`role_definition2` = '对抗路')) or ((`r1`.`role_definition2` = '辅助') and (`r2`.`role_definition2` = '对抗路')) or ((`r1`.`role_definition1` = '对抗路') and (`r2`.`role_definition1` = '辅助')) or ((`r1`.`role_definition2` = '对抗路') and (`r2`.`role_definition1` = '辅助')) or ((`r1`.`role_definition1` = '对抗路') and (`r2`.`role_definition2` = '辅助')) or ((`r1`.`role_definition2` = '对抗路') and (`r2`.`role_definition2` = '辅助')));
